import React, { useState } from "react";
import { FileTreeContainer } from "./styles";

function FileTree(props) {
  const { fileTree } = props;

  // const horPipe = "─";
  const blank = "    ";
  const iPipe = " |  ";
  const tPipe = " ├──";
  const lPipe = " └──";

  const writeLines = ({ treeNode, isLastChild, parentWasLastChild, inheritedPrefix }) => {
    const pipeOrBlank = parenWasLastChild ? blank : pipe;
    const tpipeOrLpipe = isLastChild ? lPipe : tPipe;
    const nodeLine = "<p>" + inheritedPrefix + pipeOrBlank + tpipeOrLpipe + treeNode._name + "</p>";

    let childrenLines = "";
    treeNode.entries.forEach(entry => {
      const key = entry[0]
      const child = entry[1]
      childrenLines = writeLines();
    });


    return childrenLines + nodeLine;
  };

  const writeLine = (treeNode, col = 0) => {};

  return (
    <FileTreeContainer>
      <code>{JSON.stringify(fileTree)}</code>
    </FileTreeContainer>
  );
}

export default FileTree;
