import React, { useState } from "react";
import { FileTreeContainer } from "./styles";

function FileTree(props) {
  const { fileTree } = props;

  // const horPipe = "─";
  const blank = "    ";
  const iPipe = " |  ";
  const tPipe = " ├──";
  const lPipe = " └──";

  const getNodeChildren = parentNode => {
    const parentNodeEntries = Object.entries(parentNode);
    const children = parentNodeEntries.filter(child => {
      console.log(child)
      if (child[0] !== "_name") return child[1];
    });

    return children;
  };

  const writeChildrenLines = (parentNode, parentWasLastChild, prefixToBestow) => {
    const children = getNodeChildren(parentNode);
    let childrenLines = "";

    if (children.length === 0) return childrenLines;

    children.forEach((child, index) => {
      const treeNode = child;
      const isLastChild = index === children.length - 1;
      const inheritedPrefix = prefixToBestow;

      const childInfos = { treeNode, isLastChild, parentWasLastChild, inheritedPrefix };
      childrenLines += writeLines(childInfos);
    });

    return childrenLines;
  };

  const writeLines = nodeInfos => {
    // get writeLines parameters with default values
    let { treeNode, isLastChild, parentWasLastChild, inheritedPrefix } = nodeInfos;
    isLastChild = isLastChild || true;
    parentWasLastChild = parentWasLastChild || true;
    inheritedPrefix = inheritedPrefix || "";

    // write node line
    const pipeOrBlank = parentWasLastChild ? blank : iPipe;
    const tpipeOrLpipe = isLastChild ? lPipe : tPipe;
    const nodeLine = "<p>" + inheritedPrefix + pipeOrBlank + tpipeOrLpipe + treeNode._name + "</p>";

    // write children lines if node really has children
    const prefixToBestow = inheritedPrefix + pipeOrBlank;
    const childrenLines = writeChildrenLines(treeNode, isLastChild, prefixToBestow);

    return childrenLines + nodeLine;
  };

  return (
    <FileTreeContainer>
      <code>{writeLines({treeNode: fileTree})}</code>
      {/* <code>{JSON.stringify(fileTree)}</code> */}
    </FileTreeContainer>
  );
}

export default FileTree;
